import { Component, HostListener, OnInit } from '@angular/core';
import { ApiService } from './services/api.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  userData: any;
  userRepos: any[] = []; // Array to store user repositories
  user_name: string = ''; // Initial value for name
  activePage: number | undefined;
 maxItemsPerPage: number = 10;
 itemsPerPageOptions: number[] = [10, 25, 50, 100]; // Permitted items per page options


  constructor(private apiService: ApiService) {}

  ngOnInit() {
    this.fetchUserData(this.user_name);
  }

  // Method to fetch user data
  fetchUserData(name: string) {
    this.apiService.getUser(this.user_name).subscribe((data: any) => {
      this.userData = data;
      console.log(data);
      const reposUrl = data.repos_url;
      this.fetchUserRepos(reposUrl);


    });
  }

  // Method to fetch user data


  // Method to fetch user repositories
  fetchUserRepos(reposUrl: string) {
    this.apiService.getRepos(reposUrl).subscribe((repos: any[]) => {
      this.userRepos = repos;
      // console.log('User Repositories:', this.userRepos);


        const repo = this.userRepos;

        console.log(repo);

    });
  }

  updateUserName() {
    // This method will be triggered when the "Submit" button is clicked
    // Update the user_name with the input value, and then fetch user data
    this.fetchUserData(this.user_name);
  }

  // Method to change the name and fetch new user data
  changeNameAndFetchData(newName: string) {
    this.user_name = newName;
    this.fetchUserData(this.user_name);
  }



  changeItemsPerPage(newItemsPerPage: number) {
    if (newItemsPerPage <= this.maxItemsPerPage) {
      this.maxItemsPerPage = newItemsPerPage;
      this.fetchUserRepos(this.userData.repos_url);
    }
  }

  @HostListener('window:keydown', ['$event'])
  handleKeyboardEvent(event: KeyboardEvent) {
    if (event.key === 'Enter') {
      this.updateUserName();
    }
  }
}
  <div class="main">


    <div class="search-container">
      <input
        type="text"
        class="search-box"
        placeholder="Search..."
        [(ngModel)]="user_name"
      >
      <button class="search-button" (click)="updateUserName()">Submit</button>
    </div>




      <!----- container1   start---->

    <div class="container1">

      <div class="column1">

          <img class="column-content1" [src]="userData?.avatar_url" alt="User Avatar" />

        <div class="column-content2">
          <p> {{userData?.url}} </p>
        </div>
      </div>
      <div class="column1">
        <p class="name" >{{userData?.name}}</p>
        <p class="bio">{{userData?.bio}}</p>
        <img src="assets/images/location.png" height="10" width="10"> {{userData?.location}}
        <p class="twitter"> {{userData?.twitter_username}}        </p>
      </div>
    </div>

      <!---- end container1-->





      <div class="container4">
        <h2>User Repositories</h2>
        <div class="repository-grid">
          <div *ngFor="let repo of userRepos; let i = index" class="repository-item">
            <h3>{{ repo.name }}</h3>
            <p>{{ repo.description }}</p>
            <button class="btn2"> {{repo.language}}</button>
            <p>URL: <a [href]="repo.html_url" target="_blank">{{ repo.html_url }}</a></p>
            <!-- Add more information you want to display -->
          </div>
        </div>
      </div>





    <div class="container3">
       <div class="pagination">


        <nav aria-label="Page navigation example">
          <ul class="pagination">
            <li class="page-item">
              <a class="page-link" href="#" aria-label="Previous" disabled>
                <span aria-hidden="true">&laquo;</span>
              </a>
            </li>
            <li class="page-item"><a class="page-link active" href="#">1</a></li>
            <li class="page-item"><a class="page-link" href="#">2</a></li>
            <li class="page-item"><a class="page-link" href="#" disabled>3</a></li>
            <li class="page-item">
              <a class="page-link" href="#" aria-label="Next">
                <span aria-hidden="true">&raquo;</span>
              </a>
            </li>
          </ul>
        </nav>





          </div>

  <div class="btn">
    <button>Older</button>      <button>Newer</button>   </div>

      </div>






  </div>
