import { Component, HostListener, OnInit } from '@angular/core';
import { ApiService } from './services/api.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {






   // Add these properties to your component class
totalItems: number = 1;
currentPage: number = 1;

  userData: any;
  userRepos: any[] = [];
  user_name: string = '';
  activePage: number = 1;
  totalRepos: number = 1;
  perPage: number = 10; // Default per page
  totalPages: number = 1;


  constructor(private apiService: ApiService) {
    this.currentPage = 1; // Initialize currentPage to 1
  }


  ngOnInit() {
    this.fetchUserData(this.user_name);

  }


  fetchUserData(name: string) {
    this.apiService.getUser(this.user_name).subscribe((data: any) => {
      this.userData = data;
      this.totalItems = data.public_repos;

      // Fetch user repos for the current page
      this.fetchUserRepos(data.repos_url, this.currentPage, this.perPage);
    });
  }

  fetchUserRepos(reposUrl: string, page: number, itemsPerPage: number) {
    const startIndex = (page - 1) * itemsPerPage;
    const apiUrl = `${reposUrl}?page=${page}&per_page=${itemsPerPage}`;
    this.apiService.getRepos(apiUrl).subscribe((repos: any[]) => {
      this.userRepos = repos;
    });
  }

 // Method to update the page when a page link is clicked
 changePage(action: string | number) {
  if (action === 'prev') {
    if (this.currentPage > 1) {
      this.currentPage--;
    } else {
      // Handle when the user is on the first page
      alert('You are already on the first page.');
    }
  } else if (action === 'next') {
    if (this.currentPage < this.totalItems) {
      this.currentPage++;
    } else {
      // Handle when the user is on the last page
      alert('You are already on the last page.');
    }
  } else if (typeof action === 'number' && action >= 1 && action <= this.totalItems) {
    this.currentPage = action;
  }
  this.fetchUserData(this.user_name);

}




getPages(totalItems: number, perPage: number): number[] {
  const totalPages = Math.ceil(totalItems / perPage);



  return Array.from({ length: totalPages }, (_, i) => i + 1);
}







  updateUserName() {
    // This method will be triggered when the "Submit" button is clicked
    // Update the user_name with the input value, and then fetch user data
    this.fetchUserData(this.user_name);
  }

  // Method to change the name and fetch new user data
  changeNameAndFetchData(newName: string) {
    this.user_name = newName;
    this.fetchUserData(this.user_name);
  }







  @HostListener('window:keydown', ['$event'])
  handleKeyboardEvent(event: KeyboardEvent) {
    if (event.key === 'Enter') {
      this.updateUserName();
    }
  }
}
<div class="main">
  <div class="search-container">
    <input
      type="text"
      class="search-box"
      placeholder="Search..."
      [(ngModel)]="user_name"
    >
    <button class="search-button" (click)="updateUserName()">Submit</button>
  </div>

  <!-- container1 start-->
  <div class="container1">
    <div class="column1">
      <img class="column-content1" [src]="userData?.avatar_url" alt="User Avatar" />
      <div class="column-content2">
        <p> {{userData?.url}} </p>
      </div>
    </div>
    <div class="column1">
      <p class="name">{{userData?.name}}</p>
      <p class="bio">{{userData?.bio}}</p>
      <img src="assets/images/location.png" height="10" width="10"> {{userData?.location}}
      <p class="twitter">{{userData?.twitter_username}}</p>
    </div>
  </div>
  <!-- end container1-->

  <div class="container4">
    <h2>User Repositories</h2>
    <div class="repository-grid">
      <div *ngFor="let repo of userRepos; let i = index" class="repository-item">
        <h3>{{ repo.name }}</h3>
        <p>{{ repo.description }}</p>
        <button class="btn2"> {{repo.language}}</button>
        <p>URL: <a [href]="repo.html_url" target="_blank">{{ repo.html_url }}</a></p>
        <!-- Add more information you want to display -->
      </div>
    </div>
  </div>

  <div class="container3">
    <!-- Pagination -->

    <ul class="pagination">
      <li class="page-item">
        <a class="page-link" href="#" (click)="changePage('prev')" aria-label="Previous">
          <span aria-hidden="true">&laquo;</span>
        </a>
      </li>

      <ng-container *ngFor="let page of getPages(totalItems, perPage); let i = index">
        <li class="page-item" [class.selected]="i + 1 === selectedPage">
          <a class="page-link" href="#" (click)="changePage(i + 1)">{{ i + 1 }}</a>
        </li>
      </ng-container>

      <li class="page-item">
        <a class="page-link" href="#" (click)="changePage('next')" aria-label="Next">
          <span aria-hidden="true">&raquo;</span>
        </a>
      </li>
    </ul>









    <div class="btn">
      <button  (click)="changePage(1)">Older</button>
      <button>Newer</button>
    </div>
  </div>
</div>
